import { Command } from "./command.js";
/**
 * @see https://redis.io/commands/flushall
 */
export class FlushAllCommand extends Command {
    constructor(args, opts) {
        const command = ["flushall"];
        if (args && args.length > 0 && args[0].async) {
            command.push("async");
        }
        super(command, opts);
    }
}
