import { Command } from "./command.js";
/**
 * @see https://redis.io/commands/script-flush
 */
export class ScriptFlushCommand extends Command {
    constructor([opts], cmdOpts) {
        const cmd = ["script", "flush"];
        if (opts?.sync) {
            cmd.push("sync");
        }
        else if (opts?.async) {
            cmd.push("async");
        }
        super(cmd, cmdOpts);
    }
}
