import { defaultFS } from "@llamaindex/env";
import { Document } from "../Node.js";
/**
 * Read a .txt file
 */ export class TextFileReader {
    async loadData(file, fs = defaultFS) {
        const dataBuffer = await fs.readFile(file);
        return [
            new Document({
                text: dataBuffer,
                id_: file
            })
        ];
    }
}
