export class FunctionTool {
    _fn;
    _metadata;
    constructor(fn, metadata){
        this._fn = fn;
        this._metadata = metadata;
    }
    static fromDefaults(fn, metadata) {
        return new FunctionTool(fn, metadata);
    }
    get metadata() {
        return this._metadata;
    }
    async call(...args) {
        return this._fn(...args);
    }
}
